var m_strLowerCase = "abcdefghijklmnopqrstuvwxyz";
var m_strNumber = "0123456789";
var m_strCharacters = "!@#$%^&*?_~";

//Check password
function checkPassword(strPassword)
{
    // Reset combination count
    var nScore = 0;

    // Password length
    // -- Less than 4 characters
    if (strPassword.length < 5)
    {
        nScore += 5;
    }
    // -- 5 to 7 characters
    else if (strPassword.length > 4 && strPassword.length < 8)
    {
        nScore += 10;
    }
    // -- 8 or more
    else if (strPassword.length > 7)
    {
        nScore += 25;
    }

    // Letters
    var nUpperCount = countContain(strPassword, m_strUpperCase);
    var nLowerCount = countContain(strPassword, m_strLowerCase);
    var nLowerUpperCount = nUpperCount + nLowerCount;
    // -- Letters are all lower case
    if (nUpperCount === 0 && nLowerCount !== 0)
    {
        nScore += 10;
    }
    // -- Letters are upper case and lower case
    else if (nUpperCount !== 0 && nLowerCount !== 0)
    {
        nScore += 20;
    }

    // Numbers
    var nNumberCount = countContain(strPassword, m_strNumber);
    // -- 1 number
    if (nNumberCount == 1)
    {
        nScore += 10;
    }
    // -- 3 or more numbers
    if (nNumberCount >= 3)
    {
        nScore += 20;
    }

    // Characters
    var nCharacterCount = countContain(strPassword, m_strCharacters);
    // -- 1 character
    if (nCharacterCount == 1)
    {
        nScore += 10;
    }
    // -- More than 1 character
    if (nCharacterCount > 1)
    {
        nScore += 25;
    }

    // Bonus
    // -- Letters and numbers
    if (nNumberCount !== 0 && nLowerUpperCount !== 0)
    {
        nScore += 2;
    }
    // -- Letters, numbers, and characters
    if (nNumberCount !== 0 && nLowerUpperCount !== 0 && nCharacterCount !== 0)
    {
        nScore += 3;
    }
    // -- Mixed case letters, numbers, and characters
    if (nNumberCount !== 0 && nUpperCount !== 0 && nLowerCount !== 0 && nCharacterCount !== 0)
    {
        nScore += 5;
    }


    return nScore;
}

//Runs password through check and then updates GUI
function runPassword(strPassword, strFieldID)
{
    // Get controls
    var ctlBar = document.getElementById(strFieldID + "_bar");
    var ctlText = document.getElementById(strFieldID + "_text");
    var ctlTextInner = document.getElementById(strFieldID + "_text_inner");
    if (!ctlBar || !ctlText || !ctlTextInner) {
        return;
    }
    if (strPassword.length > 0) {
        // Check password
        var nScore = checkPassword(strPassword);

        // Set new width
        ctlBar.style.width = nScore + "%";

        // Color and text
        var icon, strText, strColor;
        // -- Very Secure
        if (nScore >= 90)
        {
            icon = 'ok';
            strText = tr("Very Secure");
            strColor = "#0ca908";
        }
        // -- Secure
        else if (nScore >= 80)
        {
            icon = 'ok';
            strText = tr("Secure");
            strColor = "#0ca908";
        }
        // -- Very Strong
        else if (nScore >= 70)
        {
            icon = 'ok';
            strText = tr("Very Strong");
            strColor = "#0ca908";
        }
        // -- Strong
        else if (nScore >= 60)
        {
            icon = 'ok';
            strText = tr("Strong");
            strColor = "#0ca908";
        }
        // -- Average
        else if (nScore >= 40)
        {
            icon = 'none';
            strText = tr("Average");
            strColor = "#e3cb00";
        }
        // -- Weak
        else if (nScore >= 25)
        {
            icon = 'error';
            strText = tr("Weak");
            strColor = "#ff0000";
        }
        // -- Very Weak
        else
        {
            icon = 'error';
            strText = tr("Very Weak");
            strColor = "#ff0000";
        }
        ctlBar.style.backgroundColor = strColor;
        $(ctlBar).show();
        if (icon === 'none') {
            $(ctlText).children('span.icon').hide();
        } else if (icon === 'ok') {
            $(ctlText).children('span.icon-ok').css('color', strColor).show();
            $(ctlText).children('span.icon-error').hide();
        } else if (icon === 'error') {
            $(ctlText).children('span.icon-ok').hide();
            $(ctlText).children('span.icon-error').css('color', strColor).show();
        }
        $(ctlTextInner).text(tr('Strength') + ': ' + strText).show();
    } else {
        $(ctlText).children().hide();
        $(ctlTextInner).hide();
        $(ctlBar).hide();
    }
}

//Checks a string for a list of characters
function countContain(strPassword, strCheck)
{
    // Declare variables
    var nCount = 0, i;

    for (i = 0; i < strPassword.length; i++)
    {
        if (strCheck.indexOf(strPassword.charAt(i)) > -1)
        {
            nCount++;
        }
    }

    return nCount;
}

