        $select
            .find('option:not(:checked)')
            .not('.protected')
            .remove();

        $noresults.toggleClass('d-none', selection.length !== 0);

        // add all galleries parent titles
        $.each(result, function (key, value) {
            if (value.parent_id) {
                value.parent_title = parentTitle(result, value.parent_id);
            }
            result[key] = value;
        });

