//Makes modal over window or object so ajax can load and user can't prevent action
$.fn.tikiModal = function(msg) {
    var obj = $(this);
    if (!obj.length) {
        return null;            // happens after search index rebuild in some conditions
    }
    var lastModal = obj.data('lastModal');

    if (!lastModal) {
        lastModal = Math.floor(Math.random() * 1000);
        obj.data('lastModal', lastModal);
    }
    var box = {
        top: obj.offset().top,
        left: obj.offset().left,
        height: obj.outerHeight(),
        width: obj.outerWidth()
    };
    var modal = $('body').find('#modal_' + lastModal);
    var spinner = $('<img src="img/spinner.gif" style="vertical-align: top; margin-right: .5em;" />');

    if (!msg) {
        modal
            .fadeOut(function() {
                $(this).remove();
            });
        obj.removeData('lastModal');
        return obj;
    }

    if (modal.length) {
        modal
            .find('.dialog')
            .empty()
            .html(spinner)
            .append(msg);
        return obj;
    }

    modal = $('<div id="modal_' + lastModal + '" class="tiki-modal">' +
                    '<div class="mask" />' +
                    '<div class="dialog"></div>' +
            '</div>')
        .appendTo('body');

    var zIndex = 0;
    if (obj.is("body")) {
        zIndex = 2147483646 - 1;    // maximum
        box.top = obj.offset().top + $window.scrollTop();
        box.left = obj.offset().left + $window.scrollLeft();
    } else {
        obj.parents().addBack().each(function () {
            var z = $(this).css("z-index");
            if (z && z !== 'auto' && z > zIndex) {
                zIndex = Number(z);
            }
        });
    }

    //Set height and width to mask to fill up the whole screen or the single element
    modal
        .width(box.width)
        .height(box.height)
        .css('top',     box.top + 'px')
        .css('left',     box.left + 'px')
        .find('.mask')
            .height(box.height)
            .fadeTo(1000, 0.6)
        .parent()
        .find('.dialog')
            .hide()
            .append(spinner)
            .append(msg);
    var dialog = modal.find('.dialog');
    if (obj.is("body")) {
        dialog.css({
            top: (box.top + $window.innerHeight()/2 - $window.scrollTop()) + "px",
            left: (box.left + $window.innerWidth()/2 - $window.scrollLeft()) + "px"
        });
    }
    dialog.css({
        marginTop: (dialog.height() / -2) + "px",
        marginLeft: (dialog.width() / -2) + "px"
    }).show();

    if (zIndex) {
        modal.css("z-index", zIndex + 1);
    }
    return obj;
};

//makes the width of an input change to the value
$.fn.valWidth = function() {
    var me = $(this);
    return me.ready(function() {
        var h = me.height();
        if (!h) {
            h = me.offsetParent().css("font-size");
            if (h) {
                h = parseInt(h.replace("px", ""));
            }
        }
        me.keyup(function() {
            var width = me.val().length * h;

            me
                .stop()
                .animate({
                    width: (width > h ? width : h)
                }, 200);
        })
        .keyup();
    });
};

//For making pagination have the ability to enter page/offset number and go
$.paginationHelper = function() {
    $('.pagenums').each(function() {
        var me = $(this);
        var step = me.find('input.pagenumstep');
        var endOffset = (me.find('input.pagenumend').val() - 1) * step.data('step');
        var url = step.data('url');
        var offset_jsvar = step.data('offset_jsvar');
        var offset_arg = step.data('offset_arg');

        me.find('span.pagenumstep').replaceWith(
            $('<input type="text" style="font-size: inherit; " />')
                .val(step.val())
                .change(function() {
                    var newOffset = step.data('step') * ($(this).val() - 1);

                    if (newOffset >= 0) {
                        //make sure the offset isn't too high
                        newOffset = (newOffset > endOffset ? endOffset : newOffset);

                        //THis is for custom/ajax search handling
                        window[offset_jsvar] = newOffset;
                        if (step[0]) {
                            if (step.attr('onclick')) {
                                step[0].onclick();
                                return;
                            }
                        }

                        //if the above behavior isn't there, we update location
                        document.location = url + offset_arg + "=" + newOffset;
                    }
                })
                .keyup(function(e) {
                    switch(e.which) {
                        case 13: $(this).blur();
                    }
                })
                .valWidth()
        );
    });
};

//a sudo "onvisible" event
$.fn.visible = function(fn, isOne) {
    if (fn) {
        $(this).each(function() {
            var me = $(this);
            if (isOne) {
                me.one('visible', fn);
            } else {
                me.on('visible', fn);
            }

            function visibilityHelper() {
                if (!me.is(':visible')) {
                    setTimeout(visibilityHelper, 500);
                } else {
                    me.trigger('visible');
                }
            }

            visibilityHelper();
        });
    } else {
        $(this).trigger('visible');
    }

    return this;
};

$.download = function(url, data, method){
    //url and data options required
    if( url && data ){
        //data can be string of parameters or array/object
        data = typeof data == 'string' ? data : jQuery.param(data);
        //split params into form inputs
        var inputs = '';
        jQuery.each(data.split('&'), function(){
            var pair = this.split('=');
            inputs+='<input type="hidden" name="'+ pair[0] +'" value="'+ pair[1] +'" />';
        });
        //send request
        jQuery('<form action="'+ url +'" method="'+ (method||'post') +'">'+inputs+'</form>')
        .appendTo('body').submit().remove();
    }
};

