function delayedExecutor(delay, callback)
{
    var timeout;

    return function () {
        var args = arguments;
        if (timeout) {
            clearTimeout(timeout);
            timeout = null;
        }

        timeout = setTimeout(function () {
            callback.apply(this, args);
        }, delay);
    };
}

