$.fn.moveToWithinWindow = function() {
    var $el = $(this);
    var h = $el.height(),
    w = $el.width(),
    o = $el.offset(),
    po = $el.parent().offset(),
    st = $window.scrollTop(),
    sl = $window.scrollLeft(),
    wh = $window.height(),
    ww = $window.width();

    if (w + o.left > sl + ww) {
        $el.animate({'left': sl + ww - w - po.left}, 'fast');
    }
    if (h + o.top > st + wh) {
        $el.animate({'top': st + wh - (h > wh ? wh : h) - po.top}, 'fast');
    } else if (o.top < st) {
        $el.animate({'top': st - po.top}, 'fast');
    }
};

$.fn.scaleImg = function (max) {
    $(this).each(function() {
        //Here we want to make sure that the displayed contents is the right size
        var h, w, img = $(this),
        actual = {
            height: img.height(),
            width: img.width()
        },
        original = $(this).clone(),
        parent = img.parent();

        var winner = '';

        if (actual.height > max.height) {
            winner = 'height';
        } else if (actual.width > max.width) {
            winner = 'width';
        }

        //if there is no winner, there is no need to resize
        if (winner) {
            //we resize both images and svg, we check svg first
            var g = img.find('g');
            if (g.length) {
                img
                    .attr('preserveAspectRatio', 'xMinYMin meet');

                parent
                    .css('overflow', 'hidden')
                    .width(max.width)
                    .height(max.height);

                g.attr('transform', 'scale( ' + (100 / (actual[winner] / max[winner]) * 0.01)  + ' )');
            } else {
                //now we resize regular images
                if (actual.height > actual.width) {
                    h = max.height;
                    w = Math.ceil(actual.width / actual.height * max.height);
                } else {
                    w = max.width;
                    h = Math.ceil(actual.height / actual.width * max.width);
                }
                img.css({ height: h, width: w });
            }

            img
                .css('cursor', "url(img/icons/zoom.gif),auto")
                .click(function () {
                    $('<div/>').append(original).dialog({
                        modal: true,
                        width: Math.min($(window).width(), actual.width + 20),
                        height: Math.min($(window).height(), actual.height + 50)
                    });
                    return false;
                });
        }
    });

    return this;
};


