    tableHTML = tableHTML.replace(/\s*(<\/{0,1}(?:thead|tbody|tr|th|td)(?:| [^>]*)>)\s*/igm, '$1');
    tableHTML = tableHTML.replace(/\s*(\s<\/{0,1}(?:p|br|div|i|b)(?:| [^>]*)>)/igm, '$1');
    tableHTML = tableHTML.replace(/(<\/{0,1}(?:p|br|div|i|b)(?:| [^>]*)>\s)\s*/igm, '$1');

